import { useEffect, useState, useTransition } from "react";

function App() {
	const [loading, setLoadint] = useState(true);
	useEffect(() => {
		fetch('https://api.coinpaprika.com/v1/tickers');
	}, []);
	return (
		<div>
			<h1>The Coins!</h1>
			{loading ? <strong>Loading...</strong> : null}
		</div>
	);
}

export default App;
